INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (1, '仙尊破魔', '童子佬', '神秘的少年从山中走出，没有记忆的他被陌生家庭接纳，天生的神力，惊现的神术，逆天的修行资质，随着少年慢慢地成长，一个个秘密也是接踵而来，属于他的使命，终将要他来完成。', 3, '连载中', '2018-10-18', NULL, 1, 234, 200, 915182, 'image/586262.jpg', '1');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (2, '直播之狂暴升级', '看电视吃瓜子', '峰至尊，绝代天骄是种罪恶，我已经恶贯满盈…… \r\n如果穿越不是为了装逼，那将毫无意义…… \r\n如果击败巅峰至尊，绝代天骄是种罪恶，我已经恶贯满盈…… \r\n如果引得冰山女神，呆萌萝莉，性感郡主一见倾心是我的过错，我真是大错特错…… \r\n杀，就杀个满门尽灭，装，就装个天下无敌，惹，就惹个美人心醉！ \r\n不服就干是我的为人处事，狂妄嚣张是我的人生格言，且看装逼高手张东成，获得直播升级系统后，如何肆无忌惮横行异界大陆，书写属于他的登天之路，狂暴传奇！ \r\n本书原名《直播之狂暴升级》，老读者们见谅，改了书名……', 3, '连载中', '2016-09-18', NULL, 1, 567, 400, 3679761, 'image/382194.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (3, '争霸仙途', '品茗闲人', '得女娲娘娘眷顾，受上天仙界垂青，混沌世界，张天兴乘势而起，斩妖魔，斗鬼怪，挑战一切权贵，藐视六界法统！携剑长歌，轻狂世界独善其身，五行之地任意翱翔！妖是情丝媚骨潋滟花开！魔是桀骜浪荡纵横四海！鬼是夜半青灯旧恩难捱！怪是韶颜如初与君徘徊！开山斧造就新世界，永恒信念铸造无上至尊', 3, '连载中', '2019-04-06', NULL, 1, 567, 300, 207776, 'image/661593.jpg', '5');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (4, '万古狂尊', '一壶酒', '一代杀神江枫，叱咤十方风云，却在探索仙古遗迹之时，被红颜知己暗算，携旷世古经，重生八千年后！\r\n一世情断，三生缘尽，心怀无上杀意，江枫强势崛起，凝聚九龙星象，淬炼不灭之躯，以无敌身姿，暴揍各路天骄！\r\n我意，神不可挡，我念，天不可灭，这一世，吾必杀他个血染青霄，不上九天终不还！', 3, '连载中', '2017-07-05', NULL, 2, 891, 789, 2505851, 'image/415816.jpg', '7');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (5, '太古雷龙觉', '细浪', '璀璨星空，万族林立！ \r\n仙界大战，无上帝族被十六仙盟，联合绞杀殆尽之时，却有一名族人意外落入了一片仙界之下的三千炼狱，侥幸得以生存…… \r\n若干年后，炼狱世界的一个神秘的隐世家族中，一名被家族遗弃的废材少年，意外觉醒龙脉，并在机缘巧合之下获得了，曾经无上大族的一页绝世经文，从此以后，他走上了艰难的逆天改命之路…… \r\n本书，且看废材少年，如何凭一己之力，踏破这天地的牢笼，一步一步走上万族之首，最终屹立星空之巅，破除长生劫，只身踏入那传说中的极道圣境……', 3, '连载中', '2019-05-20', NULL, 1, 789, 673, 2467394, 'image/473870.jpg', '5');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (6, '万界情绪收集商', '红烧快鱼', '什么？挨打能变强？ \r\n挨打还能有钱？ \r\n不！我不是那种人！ \r\n你说挨打能变帅？ \r\n说吧，怎么打。 \r\n唉，唉！别打脸啊！', 5, '连载中', '2019-02-18', NULL, 1, 567, 500, 948789, 'image/643960.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (7, '鬼媳妇', '雨天清河 ', '我十岁那一年，村里闹了饥荒，爷爷为某活路，无意间收下了狐仙的聘礼。八年后，她来娶我了，可是爷爷却让我入赘给一个死人', 8, '连载中', '2019-01-28', NULL, 2, 800, 400, 673600, 'image/635357.jpg', '3·');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (8, ' 凡道至尊', '人匕十杰', '逆天的口号从不宣之于口，但凭实力将这天踩在脚下！ \r\n一个机缘巧合下步入修行界的平凡少年，且看他如何超凡脱俗，得证大道！ \r\n略慢热，但精彩；有金手指，但不BUG！', 10, '连载中', '2019-02-23', NULL, 1, 7000, 3000, 505068, 'image/640747.jpg', '1');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (9, '武炼丹尊', '免费的午餐', '一世丹尊、苏凌，偶获上古至宝天魔珠，却惨遭奸人围攻陨落。\r\n五百年后，苏凌重生在一个苏家的废物少爷身上，从此踏上一条逆天之路。\r\n修炼丹武之道，何人敢称至尊，唯我苏凌', 3, '连载中', '2018-10-25', NULL, 2, 4000, 600, 1417760, 'image/588643.jpg', '4');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (10, '荒天神帝', '枇杷', '持神器、御灵兽、战苍天、斗神魔，且看废土之中走出的少年如何创造属于自己的神话……', 3, '连载中', '2017-05-31', NULL, 1, 4901, 3608, 3427998, 'image/417818.jpg', '3');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (11, '修真三国', '郭李配', '这是来自异界的三国，同样的热血同样的激情。在这里，不再有郭嘉不死卧龙不出的遗憾，有的是化身为牛头人的高顺带领着牛头人战士的陷阵营与化身为狂战士的先登死士之间激烈的碰撞，有的是身为精灵圣女的貂蝉与翼人首领吕布之间的坎坷情路，有的是龙人关羽在使命与魔化了的刘备张飞兄弟情之间的挣扎……', 3, '连载中', '2019-02-01', NULL, 1, 346, 345, 427423, 'image/645180.jpg', '3');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (12, '妖妻', '我会开拖拉机', '一次偶然，我结识狐仙，从此，立堂口，破红尘，收山神当弟子，奴鬼差渡生死……随着遇到的事情越来越多，我遇到的妖怪也千奇百怪，只不过他们都成了我的出马仙', 8, '连载中', '2019-01-24', NULL, 1, 256, 200, 633956, 'image/633946.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (13, '万魔龙帝', '关月', '魔界三大主宰之一魔龙主宰遭亲信背叛，含恨陨落，重生凡界神武界。\r\n修魔族至尊功法《魔龙不灭功》，亿万龙力傍身，脚踏星河、剑破九霄，鏖战万界。\r\n一路征服，扮猪吃虎，拳打圣子、脚踢神帝，专治各种不服。\r\n收圣女、俘仙子，红颜祸水、绝世妖妃，一个都不放过！【帝盟总群：511-135-231，大量空位，欢迎加入！】', 3, '连载中', '2019-02-11', NULL, 2, 347, 289, 719152, 'image/640793.jpg', '3');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (14, '清末小侯爷', '炎萌萌主', '【清穿不造反，菊花套电钻】牛人徐凡意外重生清末，在第二次鸦片战争即将到来的时局下，一套现代兵法出神入化，打得英法德日俄泪流满面；一手权谋步步惊心，在清末翻手为云覆手为雨；平太平天国，灭爱新觉罗，建设近代工业，开资产先河，杀尽清狗鞑夷，横扫全球列强，扬眉吐气一雪国耻，犯我中华者，虽远必诛', 4, '连载中', '2018-11-27', NULL, 1, 578, 300, 960019, 'image/602365.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (15, '剑翱九霄', '云傲天', '少小便遭满门斩，老天留情遇贵人。 \r\n青梅竹马伴身旁，红颜知己君多情。 \r\n天赋异禀还勤奋，何时逢困不前行？ \r\n自古英雄出少年，传奇人生难言尽。', 3, '连载中', '2019-01-25', NULL, 2, 456, 235, 481509, 'image/634314.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (16, '重生之逆流人生', '楸楸糖', '上辈子生的委屈、长得憋屈、活的像个臭虫、死的时候还被人指指点点！\r\n这辈子指点江山、豪情壮志、爱江上更爱美人！\r\n上辈子欠你的！这辈子还！上辈子欠我的，我给你好看！\r\n没有金手指的神通、没有小鲜肉的魔力、没有偶遇校花、村花、主播的想法！\r\n我就是我，腊月初八被人点的烟火！\r\n女作者写的男主书，细腻的笔体、真实的生活、不服来看！', 5, '连载中', '2018-12-26', NULL, 1, 965, 89, 778004, 'image/619264.jpg', '4');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (17, '医触即发', '紫菲子', '一场意外铸成一个执念，简欣如愿进入仁济医院急诊科，机缘巧合下他成了她的带教，从此开启一条与死神争分夺秒的', 15, '连载中', '2019-03-01', NULL, 1, 346, 23, 174370, 'image/647997.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (18, '心理治愈师', '艾镜', '苏木原以为她的人生永远不会和危险扯上关系。\r\n当那个穿着黑色斗篷的男人出现在她面前时，周遭的一切都变得分外陌生，好像从前活着的那些年不过做了一场虚假梦境。\r\n失去的记忆一点一点浮现在她的脑海中，原来真正自欺欺人的，从来不是别人。', 8, '连载中', '2019-04-29', NULL, 1, 456, 235, 177864, 'image/662555.jpg', '3');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (19, '医念永恒', '江月初年', '医生，掌握医药卫生知识，从事疾病预防和治疗的专业人员的统称。\r\n有这么一类人，梦想着帮助更多需要帮助的人，抱着永恒的信念一步一脚印。\r\n而魏初只是无数这样人中一个渺小的缩影。\r\n她见证了许多事情的发生与落幕，始终秉持着自己坚定的信念。', 15, '连载中', '2019-02-01', NULL, 1, 652, 365, 222049, 'image/620164.jpg', '8');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (20, '农村少女奋斗记', '一生追求卓越', '平，出生于一个贫寒的农村家庭，自幼聪慧好学，是出了名的村里的乖乖女。在她就读初三时候，父母为了给哥哥娶媳妇买车，决定让她辍学。这个如雷的消息，打破了一个拥有梦儿般的青春少女。本来一心想走出农村的女孩，为了自己的梦想，走过了崎岖坎坷的道路，为自己的人生，创造了不平凡的奇迹！', 14, '连载中', '2019-03-30', NULL, 1, 690, 360, 83396, 'image/659105.jpg', '4');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (21, '山河昭影', '月望兔', '山河漫漫，长夜无尽，此路荆刺遍布，豺狼满地，角落藏满龌龊，风雨飘散血腥。明知如此，你是否仍将怀揣一点星火，手持一把白刃，攀越江流大川，登上神圣法庭，将那星火献与你灵魂深处的信仰？ 她以命宣誓作答：此生不渝。', 15, '连载中', '2019-04-01', NULL, 1, 570, 340, 204588, 'image/647730.jpg', '3');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (22, '凌天战神', '万木峥嵘', '凌天大陆，星辰皆有战灵，天才认主，武道觉醒。强者一念，天地色变，弱者如蚁，血洒苍穹！更有传说，凌天之上，有龙翱翔。上古武帝庄子，九转涅槃后悟屠龙之技，御龙为马，主宰天地，是为凌天战神。 “天下苍生视我为敌又如何？” 叶峰紧握长枪，怒视苍穹之上：“他日我为玄帝，敢踏破苍天大地！”作者QQ:482146149 群聊号码：743713487（作者朋友勿入', 3, '连载中', '2016-12-12', NULL, 1, 389, 200, 7700400, 'image/393057.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (23, '武神圣帝', '翎晨', '古凰涅槃造化生，玄经衍化天骄成！\r\n万界争锋未尝败，武神铸就圣帝名！\r\n少年天骄显峥嵘，傲气凌云霄，武道灭万世！\r\n举手投足间苍生寂灭，翻云覆雨时镇压诸天；一拳一掌崩灭诸天星辰，一念一怒众生血流成河!\r\n萧晨，欲成为九天之上最亮的一颗星...', 3, '连载中', '2017-08-14', NULL, 1, 567, 346, 3708465, 'image/434683.jpg', '1');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (24, '龙魂战尊', '独孤求醉', '太古初期，神龙帝域相争，龙尸遍野，青龙爆体，六条神龙化作龙珠散落人间。\r\n少年龙炎从小缺失武魂，意外得到神龙残魂，开启武道传承。为追逐爱情，解析龙魂身世之谜，终踏上争霸天下之路。', 3, '连载中', '2018-03-01', NULL, 1, 567, 234, 5465290, 'image/437074.jpg', '1');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (25, '人屠归来', '银眸沙皇', '地下凶神，回归华夏，加入了特殊组织。却因过往的经历，被关入了黑狱。走出黑狱的他，究竟会在都市中，掀起怎样的腥风血雨......', 5, '连载中', '2018-05-23', NULL, 1, 123, 100, 2469280, 'image/525900.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (26, '绝世符神', '东方行云', '“你能炼药，可以快速突破？我一枚聚灵符，能增加十倍百倍的修炼速度！”\r\n“你会炼器，炼出神兵可斩星辰？我有九天雷符，能召唤九天神雷，一击之下万里成灰！”\r\n“你精通阵法，可攻可守？我有铭符万千，他人生死皆在我一念之间！”\r\n万年之前的九天至尊殒落身死，却转世重生于万年之后，成了一个三流家族中人人可欺的庸人。\r\n一次重伤，萧羽想起了前世所有记忆，无论是炼药还是锻器，各道都是信手拈来，然而他最为擅长的，始终是铭符之道', 3, '连载中', '2016-12-20', NULL, 1, 234, 123, 3588063, 'image/393633.jpg', '3');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (27, '布衣神卦', '火了大宝剑', '伍卜通毕生目标就是：泡最美的妞，踩最狂的人。 \r\n当然，为了目标他毅然决然的靠着血脉传承，踏上了最有前途的职业——算命。\r\n“小姐姐，我看你眼眶发黑，要不要给你算一卦啊，百卦百灵的。” \r\n“深井冰啊，我这是烟熏妆！”', 5, '连载中', '2019-03-15', NULL, 1, 156, 147, 344054, 'image/653674.jpg', '4');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (28, '窃朝', '向右看', '什么？你说我坑了你两万两？ \r\n什么？你说我抢了你的心上人？ \r\n什么？你说我毁了你的仕途？ \r\n…… \r\n公子，你可别乱说，咱是一个善良的人！ \r\n怎么证明？ \r\n来呀，把这满嘴谎话妖言惑众的公子打死！', 4, '连载中', '2019-04-18', NULL, 1, 167, 148, 141926, 'image/665440.jpg', '4');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (29, '典当聊天群', '呓木风雨 ', '什么！你要开当铺？\r\n是的，叶言决定开一家世间最不一样的当铺。\r\n这是一家潮流当铺，在聊天群的见证下，记录着一个又一个故事。', 3, '连载中', '2019-04-21', NULL, 1, 189, 124, 108365, 'image/666729.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (30, '八界幼儿园', '大胖罗兜', '同学们，这是你们的新老师，来来来都做下自我介绍……\r\n我叫牛二黑，我哥是牛红，我爹是牛魔王，娘亲芭蕉公主。尼玛标准的妖二代！\r\n我叫孙悟饭，我爹是斗战胜佛。额，好吧，我知道了，你是佛二代。', 10, '连载中', '2019-04-29', NULL, 1, 179, 159, 98308, 'image/665600.jpg', '3');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (31, '符道至尊', '一页沧海', '天符大陆，宗门林立，百族并存，无奇不有，而以符道最为玄妙。\r\n轻身符，让你日行千里。\r\n铁甲符，让你刀枪不入。\r\n降妖符，让你降妖除魔。\r\n地球上符道造诣最深的道士，临终前凭借一张上古神符重生异世，凭借一身符道本领逆袭至尊。\r\n任你妖魔鬼怪，我以一符封之！', 3, '连载中', '2019-04-23', NULL, 1, 199, 155, 185818, 'image/662232.jpg', '1');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (32, '厨神', '桃园公子', '妖魔横行的世界，一个小乞丐居然咸鱼翻身，凭一双擀面杖就想复仇，美食派系间的千古恩仇，六界神厨谁与争锋！', 3, '连载中', '2019-05-01', NULL, 1, 800, 677, 100065, 'image/669383.jpg', '1');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (33, '龙王胎', '银花火树', '1934年，奶奶亲眼目睹了一场离奇的坠龙事件……\r\n多年以后，我身上长出了鳞片，更可怕的是，我竟然怀上了怪胎！\r\n————\r\n领兵带队跑堂口，顶香火头搬杆子，我是个女弟马，为大家讲述我当出马弟子那些年的真实经历……', 8, '连载中', '2019-05-23', NULL, 1, 560, 55, 36719, 'image/672376.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (34, '重生之阴阳女术师', '木亦同', '【爽文，独宠】权势有多全，一走阳间风水摆摊，二走阴间百鬼开道，听鬼一言，一张白纸封印了上古遗迹。一场重生回归七年前，却拥有了不属于自己的记忆，全家极品犯贱，伤我，害我，我来教你死字怎么写。曾经欺她弱小，现在她霸气转身揍回去。身前开挂享受无比，身后某男保驾护航护妻独宠一生', 3, '连载中', '2019-01-18', NULL, 1, 555, 444, 492813, 'image/628149.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (35, '萌宝归来：首席大人宠上瘾', '淼淼测一下', '前夫和妹妹联手设计，让她身败名裂，一无所有。\r\n　　唐婉言一怒之下置之死地而后生——出国！\r\n　　五年后，携小公主回国，没想到小公主自动勾搭了个靠山爸比？\r\n　　从此以后，她就过上了娃她爸在前方虐渣打怪，她在后方吃瓜捡经验的……\r\n　　没羞没臊的生活。', 15, '连载中', '2018-09-20', NULL, 1, 777, 666, 1403561, 'image/577019.jpg', '3');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (36, '暖婚似火：傅少深深宠', '寥寥四月天', '分开的十四年间，从未停止过相互思念。 \r\n相遇的那一天，洛浅川轻笑，“傅先生，小时候的过家家都不作数的。” \r\n傅星河紧攥着洛浅川的手腕，“浅浅，你只有两个选择，一是嫁给我，二是我娶你” \r\n从遇见你开始，凛冬散尽，浅川在星河里长明。 \r\n（1V1甜宠，想要甜到你心里吖。）', 15, '连载中', '2019-04-01', NULL, 1, 666, 555, 219973, 'image/655062.jpg', '8');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (37, '爱妃别想逃，乖乖束手就擒', '	吾乃红妆', '她，身份尊贵，却迫于追杀，由此女扮男装，军中三年，助他胜敌，背后助他夺得皇位。他，本身份尊贵，为夺皇位，入军杀敌，朝廷斗奸臣，只为能更好的生存……却在最美好的时光里遇见彼此。\r\n小剧场:“爱妃，想去哪儿，又想逃到哪里去？快到朕的怀里来。”某男伸手圈住南宫以茉的柔软纤细的腰肢，邪魅的说道。\r\n某女用那双充满怒火的美眸看着他，忿忿的说道:“放开你的爪子，离我远点，否则，我让你不能人道。”\r\n“爱妃，你可得为你的性福着想啊！”某男眼含点点泪光，委屈的看着小茉茉。', 15, '连载中', '2019-02-27', NULL, 1, 865, 657, 231142, 'image/647194.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (38, '总裁归来：钟少请自重', '	林蒙水', '四年前，何晨曦有多嫌弃自己爱上了一个木头，四年后，何晨曦就有多怀念当年的木头。\r\n老天爷，你可以把高冷，禁欲系，霸道总裁范的钟亦焕还给我吗？我不需要一天到晚耍无赖，天天动不动就聊骚的小流氓。', 15, '连载中', '2019-02-27', NULL, 1, 568, 469, 404771, 'image/647369.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (39, '大道致远', '梦竟然', '因为穷，对象被她的家人绑嫁了。萧名赌了一口气，做生意致富并离开湖湾镇。可是，湖湾镇的泥土和乡亲们让他牵挂。他又回到了湖湾镇，并要带领乡亲们一同致富。', 11, '连载中', '2019-03-15', NULL, 2, 649, 378, 307728, 'image/653788.jpg', '4');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (40, '维和先锋', '蓝盔战歌 ', '我叫肖强兵，一个远征北美战乱区的维和警察，戴上“地球与橄榄枝”肩章的刹那，维护和平、打击犯罪、传播华夏文化就成了我最大的追求……面对狂徒、灾难、战争王、阴谋家和各种圈套陷阱，我不是打不死的小强，只要我到达的地方，那里就会迎来光明和希望！', 11, '连载中', '2018-11-06', NULL, 2, 444, 143, 1445729, 'image/591207.jpg', '4');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (41, '清莲', '布衣法曹', '在司法体制改革大背景下进入检察院工作的肖川，经历了一系列此前从未有过的挑战。面对改革大潮，他用自己的坚守、智慧以及热血，展示了新时代检察人的使命担当。', 11, '连载中', '2018-04-23', NULL, 2, 888, 684, 205581, 'image/607056.jpg', '2');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (42, '河庄雄起', '笑波客', '该作品以江北省冶州地区解放后的社会主义实践探索为背景，讲述了河庄村的智慧青年白天雷，在新中国改革开放前后，历经艰难曲折，成功转型，带领乡亲们不断探求真理，如何在改革开放前后的社会变革中历经艰难困苦，处理个人、集体和社会的一系列复杂矛盾，茁壮成长为一个优秀的中国共产党员，最终将河庄建成一个远近闻名的红色亿元村！为河庄的华丽蝶变成功雄起，蜡烛成灰无私奉献，终于探索出一条适合中原乡村发展的河庄道路！', 11, '连载中', '2018-08-01', NULL, 2, 978, 965, 1836803, 'image/557329.jpg', '3');
INSERT INTO `book` (`bid`, `bname`, `writer`, `breif`, `type`, `finish`, `starttime`, `endtime`, `bpermission`, `monthlynumber`, `recommend`, `words`, `picture`, `bpath`) VALUES (43, '红旗飘扬', '火树 ', '看着满目疮痍，尸横遍野的战场，那里躺着自己的兄弟、亲人、爱人……李立龙把砍卷了的大刀往地上一插，沧然泪下！ \r\n他不明白战争，但他知道一点：当无可避之时，就要勇敢面对！付出一切，在所不惜！ \r\n火树出品，值得品鉴！', 12, '连载中', '2018-11-23', NULL, 2, 999, 856, 1477567, 'image/595891.jpg', '3');
